	private static void connect() {
		String newVersion = "";
		//String key = "";
		
		try {
            HttpURLConnection connection = (HttpURLConnection) new URL("http://data.sparkle-client.tk/repo/data/update.txt").openConnection();
            connection.setRequestMethod("GET");
            newVersion = new BufferedReader(new InputStreamReader(connection.getInputStream())).readLine();
            
//            connection = (HttpURLConnection) new URL("http://data.sparkle-client.tk/repo/key.txt").openConnection();
//            connection.setRequestMethod("GET");
//            key = new BufferedReader(new InputStreamReader(connection.getInputStream())).readLine();
            
            CLIENT_VERSION = newVersion;
            //KEY = key;
           
            CLIENT_ID = "Sparkle-" + CLIENT_VERSION;
            JAR_LINK = "http://data.sparkle-client.tk/repo/client/Sparkle-" + CLIENT_VERSION +".jar";
            JSON_LINK = "http://data.sparkle-client.tk/repo/client/Sparkle-" + CLIENT_VERSION +".json";
            auth();
        } catch (MalformedURLException e) {
            e.printStackTrace();
        } catch (IOException e) {
            e.printStackTrace();
           connectionFailed();
        }
	}
	
